mapper.select:
		
		SELECT *
		FROM (
					SELECT @NO := @NO + 1 AS ROWNUM, A.*
					FROM (
    							SELECT * FROM QnATable
                  ORDER BY groupNo DESC, groupNum ASC
  				 			 ) A,
  				 			 ( SELECT @NO := 0 ) B 
				 ) C
		WHERE C.ROWNUM BETWEEN #{startIndex}+1 AND #{startIndex}+#{pageSize};

mapper.update:

  UPDATE QnATable
  SET title = #{title}
     ,qType = #{qType}
     ,uid = #{uid}
     ,content = #{content}
     ,wdate = now()
  
  WHERE qCode = #{qCode}


  UPDATE QnATable
  SET groupNum = groupNum+1
  WHERE groupNum >= #{GNpoint}
