mapper.select:

  SELECT mCode, poster, mName, genre, screen, age, s_e, country, director, actor, DD, runningTime, s_date, e_date
  FROM movieTable
  ORDER BY mName ASC;


  SELECT cineCode, brandName, cineName
  FROM cinemaTable
  ORDER BY brandName ASC, cineName ASC;

SELECT COUNT(cineCode) FROM ( SELECT CT.cineCode FROM cinemaTable CT INNER JOIN roomTable RT ON CT.cineCode=RT.cineCode INNER JOIN screenTable ST ON RT.roomCode=ST.roomCode WHERE mCode=#{mCode}
AND addr1=#{mName}
GROUP BY cineCode ) a


  SELECT cineCode, brandName, cineName
  FROM cinemaTable
  WHERE addr1=#{addr1}
  ORDER BY brandName ASC, cineName ASC;

SELECT CT.cineCode,brandName,cineName FROM cinemaTable CT INNER JOIN roomTable RT ON CT.cineCode=RT.cineCode INNER JOIN screenTable ST ON RT.roomCode=ST.roomCode WHERE mCode=#{mCode}
AND addr1=#{mName}
GROUP BY CT.cineCode ORDER BY brandName ASC, cineName ASC

SELECT * FROM cinemaTable WHERE cineCode LIKE concat(#{cineCode},'%')
AND addr1=#{addr1}
ORDER BY cineName ASC


  SELECT mCode, poster, mName, genre, screen, age, s_e, country, director, actor, DD, runningTime, s_date, e_date
  FROM movieTable
  WHERE mCode = #{mCode}
